import React,{useState,useEffect} from 'react';
import * as firebase from "firebase";

const Edit=(data,page)=>{

    const [mandalData,setMandalData]=useState();

    useEffect(()=>{
        setMandalData(data.data);
    });

    const onEdit=()=>{
        const uid = JSON.parse(localStorage.getItem('logInfo')).user.uid;
        const database = firebase.database();

        let time = new Date();
        let date = `${time.getFullYear()}년 ${time.getMonth() + 1}월 ${time.getDate()}일 ${time.getHours()}:${time.getMinutes()}:${time.getSeconds()}`;

        database.ref(`mandal/${uid}`).once('value', (snapshot) => {
            let obj = snapshot.val();
            let keyList = [];

            //키값 찾기
            for (let key in obj) {
                if (obj.hasOwnProperty(key)) {
                    keyList.push(key);
                    // console.log(key);
                }
            }
            // console.log(keyList);
            database.ref(`mandal/${uid}/${keyList[page]}`).update({
                data: mandalData,
                time: date
            });
        });
    }

    return(
        <button onClick={onEdit}>수정</button>
    );

}

export default Edit;
