import React, {Component} from 'react';
import * as firebase from "firebase";
import "firebase/auth";
import "firebase/firestore";

import { connect } from 'react-redux';
import {sign as auth} from '../../redux/action';

class Sign extends Component {
    constructor(props) {
        super(props);
        this.state={
            token:null,
            displayName:null
        }
    }

    onLogin = () => {
        var provider = new firebase.auth.GoogleAuthProvider();

        provider.addScope('https://www.googleapis.com/auth/plus.login');

        firebase.auth().signInWithPopup(provider).then((authData) => {
            console.log(authData);
            connect(state => ({ token: authData.credential.accessToken, displayName:authData.user.displayName }));
            // store.dispatch({type:LOGIN, token:authData.credential.accessToken, displayName:authData.user.displayName});
        }).catch(function (error) {
            console.log(error);
        });
    }

    onLogout = () => {
        firebase.auth().signOut().then(() => {
            connect(state => ({ token:null, displayName:null }));
        }).catch(function (error) {
            // An error happened.
        });
    }

    render() {
        const { token,displayName } = this.props;
        console.log(this.props);

        return (
            <section>
                <div>{token}님 환영합니다!</div>

                <button onClick={this.onLogin}>구글 로그인</button>
                <button onClick={this.onLogout}>구글 로그아웃</button>

            </section>
        );
    }
}
export default Sign;
