import React, {Component} from 'react';

class MandalArtTable extends Component {

    constructor(props) {
        super(props);
        this.state = {
            MandalArtData: [
                [1, 2, 3, 4, 5, 6, 7, 8, 9],
                [1, 2, 3, 4, 5, 6, 7, 8, 9],
                [1, 2, 3, 4, 5, 6, 7, 8, 9],
                ['하이', 2, 3, 4, 5, 6, 7, 8, 9],
                [1, 2, 3, 4, 5, 6, 7, 8, 9],
                [1, 2, 3, 4, 5, 6, 7, 8, 9],
                [1, 2, 3, 4, 5, 6, 7, 8, 9],
                [1, 2, 3, 4, 5, 6, 7, 8, 9],
                [1, 2, 3, 4, 5, 6, 7, 8, 9]
            ],
        }

    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    abChange = (e) => {
        this.state.ab= e.target.value;
    }
    acChange = (e) => {
        this.state.ac= e.target.value;
    }
    baChange = (e) => {
        this.state.ba= e.target.value;
    }
    bbChange = (e) => {
        this.state.bb= e.target.value;
    }
    bcChange = (e) => {
        this.state.bc= e.target.value;
    }
    caChange = (e) => {
        this.state.ca= e.target.value;
    }
    cbChange = (e) => {
        this.state.cb= e.target.value;
    }
    ccChange = (e) => {
        this.state.cc= e.target.value;
    }

    render() {
        return (
            <div className="grid outer-grid justify-between">
                {this.state.MandalArtData.map((data, idx) => {
                    return (
                        <div className="grid inner-grid justify-between items-center" key={idx}>

                            <textarea value={this.state.aa} onChange={this.onChange}/>
                            <textarea value={this.state.ab} onChange={this.onChange}/>
                            <textarea value={this.state.ac} onChange={this.onChange}/>
                            <textarea value={this.state.ba} onChange={this.onChange}/>
                            <textarea value={this.state.bb} onChange={this.onChange}/>
                            <textarea value={this.state.bc} onChange={this.onChange}/>
                            <textarea value={this.state.ca} onChange={this.onChange}/>
                            <textarea value={this.state.cb} onChange={this.onChange}/>
                            <textarea value={this.state.cc} onChange={this.onChange}/>

                            {/*{data.map((data, idx) => {*/}
                            {/*    return (*/}
                            {/*        <textarea key={idx} value={this.state.value} onChange={this.handleChange}/>*/}
                            {/*    );*/}
                            {/*})}*/}
                        </div>
                    );
                })}
            </div>

        );
    }
};

export default MandalArtTable;
