import React, {Component} from 'react';
import * as firebase from "firebase";
import { connect } from 'react-redux';
import {login,logout} from '../../redux/action';

class Sign extends Component {
    constructor(props) {
        super(props);
        this.state={
            token:null,
            displayName:null
        }
    }

    onLogin = () => {
        var provider = new firebase.auth.GoogleAuthProvider();

        provider.addScope('https://www.googleapis.com/auth/plus.login');

        firebase.auth().signInWithPopup(provider).then((authData) => {
            console.log(authData);
            this.setState({
                token:authData.credential.accessToken,
                displayName:authData.user.displayName
            });
            // store.dispatch({type:LOGIN, token:authData.credential.accessToken, displayName:authData.user.displayName});

        }).catch(function (error) {
            console.log(error);
        });
    }

    onLogout = () => {
        firebase.auth().signOut().then(() => {
            this.setState({
                token:null,
                displayName:null
            })
        }).catch(function (error) {
            // An error happened.
        });
    }

    render() {
        const { token,displayName } = this.props;
        return (
            <section>
                {/*<div>{this.state.user}님 환영합니다!</div>*/}

                <button onClick={this.onLogin}>구글 로그인</button>
                <button onClick={this.onLogout}>구글 로그아웃</button>

            </section>
        );
    }
}
export default connect(
    state => ({ token: state.token, displayName:state.displayName })
    // dispatch => ({ dispatchAddValue: amount => dispatch(login(amount)) })
)(Sign)
