import React, {Component} from 'react';
import Table from '../Table.js';
import * as firebase from "firebase";
import {List, Map, fromJS} from 'immutable';

class Detail extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: List([
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                ]
            )
        }
    }

    componentDidMount() {
        let uid = JSON.parse(localStorage.getItem('logInfo')).user.uid;

        let database = firebase.database();

        const dataList = [];

        database.ref(`/mandal/${uid}`).once('value').then((snapshot) => {
            const obj = snapshot.val();

            for (let key in snapshot.val()) {
                dataList.push(obj[key]);
            }

            this.setState({
                data: fromJS(JSON.parse(dataList[this.props.match.params.id].data))
            });
        });

    }

    onEdit = () => {
        const uid = JSON.parse(localStorage.getItem('logInfo')).user.uid;

        let database = firebase.database();

        let time = new Date();
        let date = `${time.getFullYear()}년 ${time.getMonth() + 1}월 ${time.getDate()}일 ${time.getHours()}:${time.getMinutes()}:${time.getSeconds()}`;

        let key = database.ref(`mandal/${uid}/`).getKey();


        database.ref(`mandal/${uid}/${key}`).on('value', (snapshot) => {
            const obj = snapshot.val();

            for (let key in obj) {
                if (obj.hasOwnProperty(key)) {
                    console.log("key/value", key, obj[key]);

                    // for (var obj in snapVal[key]) {
                    //
                    //     var light = snapVal[key][obj];
                    //
                    //     chartArray.push([count++, light]);
                    //
                    // }

                }
                console.log(obj);
            }
        });

        // database.ref(`mandal/${uid}/${key}`).update({
        //     data: JSON.stringify(this.state.data),
        //     time: date
        // });
    }

    onDelete = (e) => {

    }


    change = (data) => {
        this.setState({
            data: data
        });
    }

    render() {
        return (
            <section className="mandal-section">

                <Table data={this.state.data} change={this.change}></Table>

                <button onClick={this.onEdit}>수정</button>
                <button onClick={this.onDelete}>삭제</button>
            </section>
        );
    }

}

export default Detail;
