//
const AUTH = 'AUTH';

//
// export const auth=(state,action)=>({
//     type:action.type
// });

const initialState = {
    token:null,
    displayName:null,
};

//리듀서
function auth(state = initialState, action) {
    switch (action.type) {
        case AUTH:
            return {
                ...state,
                token:action.token,
                displayName: action.displayName
            };
        default:
            return state;
    }
};

export default auth;
