import {Map} from 'immutable';

//액션 타입
const LOGIN = 'LOGIN';
const LOGOUT = 'LOGOUT';

//
export const login=(state,action)=>({
    type:action.type
});
export const logout=(state,action)=>({
    type:action.type
});


export const initialState = Map({
    user:{},
});

//리듀서
const handleActions=(state = initialState, action)=> {
    switch (action.type) {
        case LOGIN||LOGOUT:
            return {
                ...state,
                user: action.user
            }

        default:
            return state;
    }
};

export default handleActions;
