import React,{Component} from 'react';

class MandalArtTable extends Component {

    constructor(props) {
        super(props);
        this.state= {
            aa:this.props.data[0],
            ab:this.props.data[1],
            ac:this.props.data[2],
            ba:this.props.data[3],
            bb:this.props.data[4],
            bc:this.props.data[5],
            ca:this.props.data[6],
            cb:this.props.data[7],
            cc:this.props.data[8],
        }
        this.handleChange=this.handleChange.bind(this);
    }



    handleChange= (e) =>{
        console.log(e.target.value);
        this.setState({
            aa:e.target.value,
            ab:e.target.value,
            ac:e.target.value,

            ba:e.target.value,
            bb:e.target.value,
            bc:e.target.value,

            ca:e.target.value,
            cb:e.target.value,
            cc:e.target.value,
        });
    }

    render() {
        return (
            <div className="grid inner-grid justify-between items-center">
                <textarea value={this.state.aa} onChange={this.handleChange}/>
                <textarea value={this.state.ab} onChange={this.handleChange}/>
                <textarea value={this.state.ac} onChange={this.handleChange}/>
                <textarea value={this.state.ba} onChange={this.handleChange}/>
                <textarea value={this.state.bb} onChange={this.handleChange}/>
                <textarea value={this.state.bc} onChange={this.handleChange}/>
                <textarea value={this.state.ca} onChange={this.handleChange}/>
                <textarea value={this.state.cb} onChange={this.handleChange}/>
                <textarea value={this.state.cc} onChange={this.handleChange}/>

                {/*{this.state.mandalData.map((data,idx)=> {*/}
                {/*    this.state.value=data;*/}
                {/*    return (*/}
                {/*        <textarea key={idx} value={data} onChange={this.handleChange}/>*/}

                {/*    );*/}
                {/*})}*/}
            </div>

        );
    }
};

export default MandalArtTable;
