    constructor(props) {
        super(props);
    }

    onLogin = () => {
        var provider = new firebase.auth.GoogleAuthProvider();

        provider.addScope('https://www.googleapis.com/auth/plus.login');

        firebase.auth().signInWithPopup(provider).then((authData) => {
            console.log(authData);
            // store.dispatch({type:LOGIN, token:authData.credential.accessToken, displayName:authData.user.displayName});

        }).catch(function (error) {
            console.log(error);
        });
    }

    onLogout = () => {
        firebase.auth().signOut().then(() => {
        }).catch(function (error) {
            // An error happened.
        });
    }

    render() {
        const { value, dispatchAddValue } = this.props;
        return (
            <section>
                {/*<div>{this.state.user}님 환영합니다!</div>*/}

                <button onClick={this.onLogin}>구글 로그인</button>
                <button onClick={this.onLogout}>구글 로그아웃</button>

            </section>
        );
    }
}

export default Sign;
