import React, {Component} from 'react';
import {Link} from 'react-router-dom';

import '../../css/header.css';
import * as firebase from "firebase";

class Header extends Component {
    onLogin=(e)=>{
        e.preventDefault();
        firebase.auth().setPersistence(firebase.auth.Auth.Persistence.LOCAL)
            .then(() => {
                var provider = new firebase.auth.GoogleAuthProvider();
                return firebase.auth().signInWithPopup(provider)
                    .then((authData) => {
                        this.setState({
                            user:authData
                        });
                        // dispatchLogin();

                        // this.props.history.push('/');
                    });
            })
            .catch((error) => {
                console.log(error);
            });
    }

    onLogout=()=>{
        localStorage.removeItem('logInfo');
    }

    render() {
        return (
            <section className="header-section">
                <div className="header-container flex justify-center justify-between items-center">
                    <Link to="/">만다라트</Link>
                    <div>
                        <span className="header-list"><Link className="mr-10" to="/mandal">만다라트 만들기</Link></span>
                        {
                            localStorage.getItem('logInfo')?
                                <>
                                    <span className="header-list"><Link className="mr-10" to="/list">리스트 보기</Link></span>
                                    <span className="cursor-pointer" onClick={this.onLogout}>로그아웃</span>
                                </>
                            : <span className="cursor-pointer" onClick={this.onLogin}>로그인</span>
                        }
                    </div>
                </div>
            </section>
        )
    }
}

export default Header;
