import React, {Component} from 'react';
import * as firebase from "firebase";

import {Link} from 'react-router-dom';

class MandalArtList extends Component {

    constructor(props){
        super(props);
        this.state={
            list:['1월 인생계획','2월','언젠']
        }
    }

    componentWillMount() {
        let userInfo=JSON.parse(localStorage.getItem('logInfo'));

        let database=firebase.database();
        database.ref(`/mandal/${userInfo.uid}`).once('value').then(function(snapshot) {
            var username = (snapshot.val() && snapshot.val().username) || 'Anonymous';
            // ...
        });
    }

    render() {
        return (
            <section className="mandal-section">
                    마이페이지
                    {
                        this.state.list.map((data,index)=>{
                            return(
                                <Link to={`/mandal/${index+1}`} key={index} className="mandal-list">{data}</Link>
                            );
                        })
                    }
            </section>
        )
    }
}

export default MandalArtList;
