import React, {Component} from 'react';
import Table from '../Table.js';
import * as firebase from "firebase";
import {List,Map} from 'immutable';

class Detail extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: List()
        }
    }

    componentWillMount() {
        let uid = JSON.parse(localStorage.getItem('logInfo')).user.uid;

        let database = firebase.database();

        database.ref(`/mandal/${uid}`).once('value').then((snapshot) => {
            const obj = snapshot.val();

            const dataList = [];
            const data=this.state.data;

            for (let key in snapshot.val()) {
                dataList.push(obj[key]);
            }

            console.log(JSON.parse(dataList[this.props.match.params.id].data));
            // data.set(0,Map(JSON.parse(dataList[this.props.match.params.id].data)));

            // this.setState({
            //     data: JSON.parse(dataList[this.props.match.params.id].data)
            // });
        });
    }

    change = (data) => {
        this.setState({
            data: data
        });
    }

    render() {
        return (
            <section className="mandal-section">
                <Table data={this.state.data} change={this.change}></Table>

                <button>수정</button>
                <button>삭제</button>
            </section>
        );
    }

}

export default Detail;
