import {Map} from 'immutable';

//액션 타입
const LOGIN = 'LOGIN';
const LOGOUT = 'LOGOUT';

//
export const login = (state, action) => ({
    type: action.type
});
export const logout = (state, action) => ({
    type: action.type
});


export const initialState ={
    user: {},
};

//리듀서
const handleActions = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                user: action.user
            }
        case LOGOUT:
            return {
                ...state,
                user: action.user
            }
        default:
            return state;
    }
};

export default handleActions;
