import React, {Component} from 'react';

class MandalArtTable extends Component {

    constructor(props) {
        super(props);
        this.state = {
            MandalArtData: [
                1, 2, 3, 4, 5, 6, 7, 8, 9
                // [1, 2, 3, 4, 5, 6, 7, 8, 9],
                // [1, 2, 3, 4, 5, 6, 7, 8, 9],
                // [1, 2, 3, 4, 5, 6, 7, 8, 9],
                // ['하이', 2, 3, 4, 5, 6, 7, 8, 9],
                // [1, 2, 3, 4, 5, 6, 7, 8, 9],
                // [1, 2, 3, 4, 5, 6, 7, 8, 9],
                // [1, 2, 3, 4, 5, 6, 7, 8, 9],
                // [1, 2, 3, 4, 5, 6, 7, 8, 9],
                // [1, 2, 3, 4, 5, 6, 7, 8, 9]
            ],
        }
    }

    onChange = (tableIndex,dataIndex,e) => {

        const { value } = e.target;

        let data = this.state.MandalArtData;
        data[tableIndex][dataIndex] = value;
        this.setState({ MandalArtData: data });

        // let data = this.state.MandalArtData;
        // data[tableIndex][dataIndex] = e.target.value;
        //
        //
        // this.setState({
        //     MandalArtData: data
        // });
    }


    render() {
        return (
            <div className="grid outer-grid justify-between">
                {this.state.MandalArtData.map((table, tableIndex) => {
                    return (
                        <div className="grid inner-grid justify-between items-center" key={table + tableIndex}>

                            {/*<textarea value={this.state.aa} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.ab} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.ac} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.ba} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.bb} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.bc} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.ca} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.cb} onChange={this.onChange}/>*/}
                            {/*<textarea value={this.state.cc} onChange={this.onChange}/>*/}


                            {table.map((data, dataIndex) => {
                                return (
                                    <input className="mandal-input" key={data + dataIndex + tableIndex} placeholder="목표" value={this.state.MandalArtData[tableIndex][dataIndex]} onChange={(e) => this.onChange(tableIndex,dataIndex,e)}/>
                                );
                            })}
                        </div>
                    );
                })}
            </div>

        );
    }
};

export default MandalArtTable;
