import {Map} from 'immutable';

//액션 이
const LOGIN = 'LOGIN';
const LOGOUT = 'LOGOUT';

//
export const login ={
    type: LOGIN
};
export const logout ={
    type: LOGOUT
};

const initialState = {
    user: {}
};

//리듀서
const handleActions = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                user: action.user,
            };
        case LOGOUT:
            return {
                ...state,
                user: null,
            };
        default:
            return state;
    }
};

export default handleActions;
