import React, {Component} from 'react';
import {List} from 'immutable';
import * as firebase from "firebase";

import Table from '../Table';
import Title from '../Title';
import Save from '../button/Save';


class Write extends Component {

    constructor(props) {
        super(props);
        this.state = {
            title: '',
            data: List([
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                    List(['', '', '', '', '', '', '', '', '']),
                ]
            )
        }
    }

    titleChange=(data)=>{
        this.setState({
            title: data
        });
    }
    tableChange = (data) => {
        this.setState({
            data: data
        });
    }

    render() {
        return (
            <section className="mandal-section ">
                <div className="container">
                    <div className="mandal-banner flex justify-between items-center mb-30">
                        <div className="font-lg font-white">
                            나만의 <span className="yellow">만다라트</span>로<br/>
                            인생을 즐겁게!
                        </div>
                        <div className="flex flex-col">

                        </div>
                    </div>
                    <Title title={this.state.title} titleChange={this.titleChange}> </Title>

                    <Table data={this.state.data} tableChange={this.tableChange}></Table>

                    <div className="text-center">
                        <Save title={this.state.title} data={this.state.data}></Save>
                    </div>

                </div>
            </section>
        );
    }
}

export default Write;
