import React, {Component} from 'react';
import Table from '../Table.js';
import * as firebase from "firebase";

class Detail extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
    }

    componentWillMount() {
        let uid = JSON.parse(localStorage.getItem('logInfo')).user.uid;

        let database = firebase.database();
        let key = database.ref(`/mandal/${uid}`).key;

        let dataList = [];

        database.ref(`/mandal/${uid}`).once('value').then((snapshot) => {
            const obj = snapshot.val();

            const dataList = [];

            for (let key in snapshot.val()) {
                dataList.push(obj[key]);
            }

            console.log(dataList[this.props.match.params.id]);
            this.setState({
                data: dataList[this.props.match.params.id]
            });
        });

        // database.ref(`/mandal/${uid}/`).on('child_added', (snapshot) => {
        //     dataList.push(JSON.parse(snapshot.val().data));
        //     console.log(dataList);
        // });
        //
        // console.log(dataList[this.props.match.params.id]);
    }

    change = (data) => {
        this.setState({
            data: data
        });
    }

    render() {
        console.log(this.state.data);
        return (
            <section className="mandal-section">
                <Table data={this.state.data} change={this.change}></Table>

                <button>수정</button>
                <button>삭제</button>
            </section>
        );
    }

}

export default Detail;
