        //     }
        //     this.setState({
        //         list: arr
        //     });
        // });
    }

    render() {
        console.log(this.state.list);
        return (
            <section className="mandal-section">
                마이페이지
                {
                    // this.state.list
                    this.state.list.map((data, index) => {
                        return (
                            <Link to={`/mandal/${index + 1}`} key={index} className="mandal-list">{index}</Link>
                        );
                    })
                }
            </section>
        )
    }
}

export default MandalArtList;
