import React, {Component} from 'react';
import * as firebase from "firebase";
import "firebase/auth";
import "firebase/firestore";

import {connect} from 'react-redux';
import {Auth} from '../../redux/action';

class Sign extends Component {
    constructor(props) {
        super(props);
        this.state = {
            token: null,
            displayName: null
        }
    }

    onLogin = (e) => {
        e.preventDefault();

        firebase.auth().setPersistence(firebase.auth.Auth.Persistence.SESSION)
            .then(function () {
                var provider = new firebase.auth.GoogleAuthProvider();

                return firebase.auth().signInWithRedirect(provider);
            }).catch(function (error) {
            console.log(error);
        });
    }

    onLogout = (e) => {
        e.preventDefault();
        firebase.auth().signOut().then(() => {
            this.setState({
                token: null,
                displayName: null
            });
            // this.connect(state => ({ token:null, displayName:null }))(Sign);
        }).catch(function (error) {
            // An error happened.
        });
    }

    render() {
        const {token, displayName, dispatchAuth} = this.props;
        console.log(this.props);

        return (
            <section>
                <div>{this.state.displayName}님 환영합니다!</div>

                <button onClick={this.onLogin}>구글 로그인</button>
                <button onClick={this.onLogout}>구글 로그아웃</button>

            </section>
        );
    }
}

export default connect(state => ({
    token: state.token,
    displayName: state.displayName
}), dispatch => ({dispatchAuth: amount => dispatch(Auth(amount))}))(Sign);
