import React,{Component} from 'react';

class MandalArtTable extends Component {

    constructor(props) {
        super(props);
        this.state= {
            mandalData : this.props.data
        }
    }

    onChange= (e) =>{
        this.setState({
            mandalData: e
        });
    }

    render() {
        return (
            <div className="grid inner-grid justify-between items-center">

                {console.log(this.state.mandalData)}
                {this.state.mandalData.map((data,idx)=> {
                    console.log(data);
                    return (
                        <textarea key={idx} value={data} onChange={this.onChange}/>

                    );
                })}
            </div>

        );
    }
};

export default MandalArtTable;
