                    value={this.state.title}
                    onChange={(e) => this.onChange(e)}
                />
                    <Table data={this.state.data} change={this.change}></Table>

                    <div className="text-center">
                        <Save title={this.state.title} data={this.state.data}></Save>
                    </div>

            </div>
        </section>
);
}
}

export default Write;
