//액션 타
const LOGIN = 'LOGIN';
const LOGOUT = 'LOGOUT';

//
export const login=(state,action)=>({
    type:action.type
});
export const logout=(state,action)=>({
    type:action.type
});입


const initialState = {
    token:null,
    displayName:null,
};

//리듀서
export default(state = initialState, action)=> {
    switch (action.type) {
        case AUTH:
            return {
                ...state,
                token:action.token,
                displayName: action.displayName
            };
        default:
            return state;
    }
};
